package array;

public class vowelsUsingArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char [] c= {'a','b','c','d','e','f','o'};
		for (char d:c)
		{
			if(d=='a'|| d=='e'||d=='i'||d=='o'|| d=='u') {
				System.out.println(d);
				
			}
		}

	}

}
